import * as THREE from './node_modules/three/build/three.module.js';
import fragment from './shaders/fragment.glsl';
import vertex from './shaders/vertex.glsl';

import mask from './images/mask.jpg';
import t1 from './images/t.png';
import t2 from './images/t1.png';

let OrbitControls = require("three-orbit-controls")(THREE);

export default class Sketch {
    constructor() {

        this.renderer = new THREE.WebGLRenderer({
            antialias: true
        });
        this.renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('container').appendChild(this.renderer.domElement);

        this.camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 3000);
        this.camera.position.z = 1000;

        this.scene = new THREE.Scene();

        this.textures = [
            new THREE.TextureLoader().load(t1),
            new THREE.TextureLoader().load(t2),
        ]
        this.controls = new OrbitControls(this.camera, this.renderer.domElement);


        this.addMesh();
        this.render();
    }

    addMesh() {
        this.material = new THREE.ShaderMaterial({
            fragmentShader: fragment,
            vertexShader: vertex,
            uniforms: {
                progress: {
                    type: "f",
                    value: 0
                },
                t1: {
                    type: "t",
                    value: this.textures[0]
                },
                t2: {
                    type: "t",
                    value: this.textures[1]
                },
            },
            side: THREE.DoubleSide,
            transparent:true,
            depthTest:false,
            depthWrite:false
        });

        let number = 512 * 512;
        // this.geometry = new THREE.PlaneBufferGeometry(1000, 1000, 10, 10);
        this.geometry = new THREE.BufferGeometry();

        this.positions = new THREE.BufferAttribute(new Float32Array(number * 3), 3);
        this.coordinates = new THREE.BufferAttribute(new Float32Array(number * 3), 3);

        // this.material = new THREE.MeshNormalMaterial({
        //     side: THREE.DoubleSide
        // });
        let index = 0;
        for (let i = 0; i < 512; i++) {
            let posX = i - 256;
            for (let j = 0; j < 512; j++) {
                this.positions.setXYZ(index, posX * 2, (j - 256) * 2, 0);
                this.coordinates.setXYZ(index, i, j, 0);
                //bez 0 byÅ‚yby tylko dwuwymiarowe
                index++;
            }
        }

        this.geometry.setAttribute("position", this.positions);
        this.geometry.setAttribute("aCoordinates", this.coordinates);

        this.mesh = new THREE.Points(this.geometry, this.material);
        this.scene.add(this.mesh);
    }

    render() {
        this.time++;
        //rotation
        // this.mesh.rotation.x += 0.01;
        // this.mesh.rotation.y += 0.02;

        // console.log(this.time)

        this.renderer.render(this.scene, this.camera);
        window.requestAnimationFrame(this.render.bind(this));
    }
}

new Sketch();
